'use strict';

class PreflightCheckError extends Error {
    constructor(message) {
        super(`${message}. Try again after fixing the problem.`);
        Object.defineProperty(this, "type", {
            enumerable: true,
            configurable: true,
            writable: true,
            value: 'preflight_check_error'
        });
    }
}

exports.PreflightCheckError = PreflightCheckError;
